                    },
                    cache: false,
                    success: function(data) {

                        // console.log(data)
                    }
                });
            }


            function trackIcon(icon, type = 'click') {
                getIconStats(icon, type)
                $.ajax({
                    type: 'POST',
                    method: 'post',
                    dataType: 'json',
                    headers: {
                        'X-CSRF-TOKEN': $('meta[name="csrf-token"]').attr('content')
                    },

                    url: '/u/trackIcon',
                    data: {

                        'icon_id': icon,
                        'action_type': type,
                        'org_hash_id': "vpVQ0Eg",
                        'csrf': "hrYLlMgToEsEPJ5MgulGOmYYUTMqBUr9joWLUsUI"
                    },
                    cache: false,
                    success: function(data) {



                    }
                });
            }


            function getIconStats(icon, type = 'click') {
                checkServer = false;
                if ($('.icon_views_' + icon).length > 0) {
                    checkServer = true

                }

                if ($('.icon_favs_' + icon).length > 0) {
                    checkServer = true

